import React, { useState, useCallback, useEffect, FC, ReactNode } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './contexts/AuthContext';
import VoiceRecorder from './components/VoiceRecorder';
import FileUploader from './components/FileUploader';
// import DiagramCanvas from './components/DiagramCanvas';
import DetailLevelSlider from './components/DetailLevelSlider';
import { LectureNotesManager } from './components/LectureNotesManager';
import { Toaster } from 'react-hot-toast';
// @ts-ignore - We'll create these files next
import Login from './pages/Login';
// @ts-ignore - We'll create these files next
import Signup from './pages/Signup';
// @ts-ignore - We'll create these files next
import Dashboard from './pages/Dashboard';
import Whiteboard from './pages/Whiteboard';
import PrivateRoute from './components/PrivateRoute';
import axios, { AxiosResponse } from 'axios';

interface AppContentProps {
  children?: ReactNode;
}

const AppContent: FC<AppContentProps> = () => {
  const { currentUser, loading } = useAuth();
  const [isRecording, setIsRecording] = useState(false);
  const [recorderStatus, setRecorderStatus] = useState('Ready');
  const [notes, setNotes] = useState('');
  const [detailLevel, setDetailLevel] = useState('standard');
  const [moduleCode, setModuleCode] = useState('');
  const [chapter, setChapter] = useState('');
  const [autoUpdateInterval, setAutoUpdateInterval] = useState<number>(0);
  const [lastUpdateTime, setLastUpdateTime] = useState<Date | null>(null);

  const processNewContent = useCallback((content: string) => {
    if (!content.trim()) return;
    setNotes(prev => prev + '\n' + content);
  }, []);

  const handleAudioData = useCallback(async (data: Blob) => {
    const formData = new FormData();
    formData.append('file', data, 'audio.wav');

    try {
      const response = await axios.post('http://localhost:8000/api/upload/audio', formData);
      if (response.data.text) {
        processNewContent(response.data.text);
      }
    } catch (error) {
      console.error('Error processing audio:', error);
    }
  }, [processNewContent]);

  const handleFileUpload = useCallback(async (result: any) => {
    if (result.text) {
      processNewContent(result.text);
    }
  }, [processNewContent]);

  const handleDiagramSave = useCallback((data: string) => {
    // TODO: Save diagram data and associate with current notes
    console.log('Diagram saved:', data);
  }, []);

  const toggleRecording = useCallback(() => {
    setIsRecording(prev => !prev);
  }, []);

  const exportNotes = useCallback(async (format: 'pdf' | 'markdown') => {
    try {
      if (format === 'pdf') {
        const response = await axios.post<Blob>('http://localhost:8000/api/export/pdf', {
          markdown: notes,
          diagrams: [] // TODO: Add saved diagrams
        }, { 
          responseType: 'blob',
          headers: {
            'Content-Type': 'application/json',
          },
        });

        const url = window.URL.createObjectURL(new Blob([response.data]));
        const link = document.createElement('a');
        link.href = url;
        link.setAttribute('download', 'notes.pdf');
        document.body.appendChild(link);
        link.click();
        link.remove();
      } else {
        const element = document.createElement('a');
        const file = new Blob([notes], { type: 'text/markdown' });
        element.href = URL.createObjectURL(file);
        element.download = 'notes.md';
        document.body.appendChild(element);
        element.click();
        element.remove();
      }
    } catch (error) {
      console.error('Error exporting notes:', error);
    }
  }, [notes]);

  const handleModuleCodeChange = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    setModuleCode(e.target.value);
  }, []);

  useEffect(() => {
    if (autoUpdateInterval > 0) {
      const timer = setInterval(() => {
        // TODO: Trigger notes regeneration
        setLastUpdateTime(new Date());
      }, autoUpdateInterval * 1000);

      return () => clearInterval(timer);
    }
  }, [autoUpdateInterval]);

  if (loading) {
    return <div className="flex items-center justify-center min-h-screen">Loading...</div>;
  }

  return (
    <div className="min-h-screen bg-gray-100">
      <Toaster position="bottom-right" />
      <Router>
        <Routes>
          <Route path="/login" element={currentUser ? <Navigate to="/dashboard" /> : <Login />} />
          <Route path="/signup" element={currentUser ? <Navigate to="/dashboard" /> : <Signup />} />

          <Route
            path="/"
            element={
              <PrivateRoute element={
                <div className="min-h-screen bg-gray-100">
                  <nav className="bg-white shadow-sm">
                    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                      <div className="flex justify-between h-16">
                        <div className="flex items-center">
                          <h1 className="text-xl font-semibold">NoteFusion AI</h1>
                        </div>
                        <div className="flex items-center space-x-4">
                          <button
                            onClick={() => exportNotes('pdf')}
                            className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
                          >
                            Export PDF
                          </button>
                        </div>
                      </div>
                    </div>
                  </nav>

                  <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                      <div className="md:col-span-2 space-y-6">
                        <div className="bg-white p-6 rounded-lg shadow">
                          <h2 className="text-xl font-semibold mb-4">Notes Editor</h2>
                          <div className="space-y-4">
                            <input
                              type="text"
                              placeholder="Module Code (e.g., ENGG1103)"
                              value={moduleCode}
                              onChange={(e) => setModuleCode(e.target.value)}
                              className="w-full px-3 py-2 border rounded"
                            />
                            <DetailLevelSlider 
                              value={detailLevel}
                              onChange={setDetailLevel}
                            />
                            <div className="mt-4">
                              <VoiceRecorder 
                                isRecording={isRecording}
                                onDataAvailable={handleAudioData}
                                onStatusChange={setRecorderStatus}
                              />
                              <FileUploader 
                                onUploadComplete={handleFileUpload}
                                acceptedTypes="audio/*,video/*,application/pdf"
                                endpoint="http://localhost:8000/api/upload/audio"
                              />
                            </div>
                          </div>
                        </div>

                        <div className="bg-white p-6 rounded-lg shadow">
                          <h2 className="text-xl font-semibold mb-4">Raw Notes</h2>
                          <textarea
                            className="w-full h-64 p-2 border rounded"
                            value={notes}
                            onChange={(e) => setNotes(e.target.value)}
                            placeholder="Your notes will appear here..."
                          />
                        </div>
                      </div>

                      <div className="space-y-6">
                        <div className="bg-white p-6 rounded-lg shadow">
                          <h2 className="text-xl font-semibold mb-4">Lecture Notes</h2>
                          <LectureNotesManager 
                            onNewNote={processNewContent}
                            currentVideoTitle={moduleCode ? `Module ${moduleCode}` : undefined}
                          />
                        </div>

                        <div className="bg-white p-6 rounded-lg shadow">
                          <h2 className="text-xl font-semibold mb-4">Whiteboard</h2>
                          <DiagramCanvas onSave={handleDiagramSave} />
                        </div>
                      </div>
                    </div>
                  </main>
                </div>
              } />
            }
          />

          <Route
            path="/dashboard"
            element={
              <PrivateRoute element={<Dashboard />} />
            }
          />

          <Route
            path="/whiteboard"
            element={
              <PrivateRoute element={<Whiteboard />} />
            }
          />

          <Route
            path="*"
            element={<Navigate to="/" replace />}
          />
        </Routes>
      </Router>
    </div>
    );
  };

// Main App component with AuthProvider
const App: React.FC = () => {
  return (
    <AuthProvider>
      <AppContent />
    </AuthProvider>
  );
};

export default App;
